import axios from "axios";
import React, { useEffect, useState } from "react";

import "./updateData.css";

const UpdateData = ({ data, onUpdate }) => {
  const [formData, setFormData] = useState({
    FirstName: data.FirstName,
    LastName: data.LastName,
    Gender: data.Gender,
    Country: data.Country,
    Age: data.Age,
    Date: data.Date,
    Id: data.Id,
  });

  useEffect(() => {
    setFormData(data);
  }, [data]);

  console.log(formData);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios
      .put("//localhost:8000/update", formData)
      .then((response) => {
        console.log(response);
      })
      .catch((e) => {
        console.log(e);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div style={{ display: "flex", margin: "40px" }}>
        <label>
          Id:
          <input
            type="text"
            name="Id"
            value={formData.Id}
            onChange={handleChange}
          />
        </label>
        <br />

        <label>
          First Name:
          <input
            type="text"
            name="FirstName"
            value={formData.FirstName}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Last Name:
          <input
            type="text"
            name="LastName"
            value={formData.LastName}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Gender:
          <input
            type="text"
            name="Gender"
            value={formData.Gender}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Country:
          <input
            type="text"
            name="Country"
            value={formData.Country}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Age:
          <input
            type="text"
            name="Age"
            value={formData.Age}
            onChange={handleChange}
          />
        </label>
        <br />
        <label>
          Date:
          <input
            type="text"
            name="Date"
            value={formData.Date}
            onChange={handleChange}
          />
        </label>
        <br />
      </div>
      <button type="submit">Update Data</button>
    </form>
  );
};

export default UpdateData;
