import { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

import "./style.css";

function isExcelFile(fileName) {
  // Lấy phần mở rộng của tên tệp
  var fileExtension = fileName.split(".").pop().toLowerCase();

  // Kiểm tra xem phần mở rộng có phải là của tệp Excel hay không
  var excelExtensions = ["xlsx", "xls"];
  return excelExtensions.includes(fileExtension);
}

export const FileUploader = ({ onSuccess }) => {
  const [files, setFiles] = useState([]);

  const onInputChange = (e) => {
    setFiles(e.target.files[0]);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    const isExcel = isExcelFile(files.name);
    if (!isExcel) {
      window.alert("Invalid file format. Please upload an Excel file.");
      return;
    }

    const data = new FormData();
    data.append("file", files);
    axios
      .post("//localhost:8000/upload", data)
      .then((response) => {
        toast.success("Upload Success");
        onSuccess(response.data[0]);
      })
      .catch((e) => {
        toast.error("Upload Error");
      });
  };

  return (
    <form method="post" action="#" id="#" onSubmit={onSubmit}>
      <div className="form-group files">
        <label>Upload Your File </label>
        <input
          type="file"
          onChange={onInputChange}
          className="form-control"
          // multiple
        />
      </div>

      <button>Submit</button>
    </form>
  );
};
