import axios from "axios";

export const getListData = async (setData, setError, setLoading) => {
  try {
    const response = await axios.get("//localhost:8000/get");
    if (response.status !== 200) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const result = response.data;
    if (result.success) {
      setData(result.data);
    } else {
      setError(result.error);
    }
  } catch (error) {
    setError(error.message);
  } finally {
    setLoading(false);
  }
};

export const uploadFile = (data) => {
  axios
    .post("//localhost:8000/upload", data)
    .then((response) => {
      console.log(response);
    })
    .catch((e) => {
      console.log(e);
    });
};

export const updateData = (formData) => {
  axios
    .put("//localhost:8000/update", formData)
    .then((response) => {
      console.log(response);
    })
    .catch((e) => {
      console.log(e);
    });
};
